import styled from "styled-components";

export const Containers=styled.div`
display: flex;
align-items: center;
justify-content: space-between;
height: 196px;
`

export const Cards=styled.div`

display: flex;
justify-content: space-between;
flex-direction: row;
align-items: center;
`
export const Img=styled.img`
width: 58px;
height: 58px;
margin-right: 15px;
`
export const Title=styled.div`
font-family: Rubik;
font-style: normal;
font-weight: 500;
font-size: 20px;
line-height: 26px;
display: flex;
align-items: center;
color: #555555;
`
