flex: 3;
height: 500px;
position: relative;
`
export const Img=styled.img`
height: 100%;
width: 100%;
background-repeat: no-repeat;
background-size: cover;
background-position: center;
z-index:-1;

`
export const Word=styled.div`
position: absolute;
top: 14%;
left: 10%;
`
export const Title=styled.div`
font-family: Mulish;
font-style: normal;
font-weight: bold;
font-size: 22px;
line-height: 26px;
display: flex;
align-items: center;
color: #555555;
`
