import styled from "styled-components";

export const Container=styled.div`
`
export const Title=styled.div`
font-family: Mulish;
font-style: normal;
font-weight: bold;
font-size: 30px;
text-align: center;
text-transform: uppercase;
color: #555555;
margin-bottom: 80px;
`
export const Wraper=styled.div`
border: 1px solid green;
padding: 20px;
z-index:-2;
`
export const Item=styled.div`
width: 816px;
height: 312px;
background: #F8FAFB;
box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.05);
border-radius: 8px;
position: relative;
z-index:-1;
`
export const Img=styled.img`
position: absolute;
width: 88px;
height: 88px;
border-radius:50%;
z-index:1;
left: 50%;
top: 0%;
`
