import styled from "styled-components";

export const Container=styled.div`
margin-top: 120px;
margin-bottom: 107px;
`
export const Title=styled.div`
font-family: Mulish;
font-style: normal;
font-weight: bold;
font-size: 36px;
line-height: 45px;
color: #555555;
text-align:center;
`
export const Ul=styled.div`
margin-top: 45px;
margin-bottom: 73px;
display: flex;
justify-content: center;
`

export const Li=styled.div`
font-family: Mulish;
font-style: normal;
font-weight: 600;
font-size: 24px;
line-height: 30px;
color: #C2C2C2;
margin-left: 90px;
transition: all 0.6s ease-in;
:hover{
    color: #7AC751;
    cursor: pointer;
    transition: all 0.6s ease-in;
}
`
export const Content=styled.div`
width: 100%;
`
